-------------------------------------------------
1. ROOT TOKENS (Tailwind plugin)
-------------------------------------------------
// tailwind.config.js
const colors = require('tailwindcss/colors')

module.exports = {
  content: ['./src/**/*.{js,ts,jsx,tsx}'],
  theme: {
    extend: {
      colors: {
        void: '#050505',
        'matrix-green': '#00FF41',
        'alert-red': '#FF3B30',
        'processing-yellow': '#FFCC00',
      },
      fontFamily: {
        mono: ['JetBrains Mono', 'monospace'],
      },
      boxShadow: {
        'glow-green': '0 0 6px #00FF41, 0 0 12px #00FF41',
        'glow-red': '0 0 6px #FF3B30, 0 0 12px #FF3B30',
        'glow-yellow': '0 0 6px #FFCC00, 0 0 12px #FFCC00',
      },
      animation: {
        'pulse-green': 'pulse-green 1.2s ease-in-out infinite',
        'pulse-red': 'pulse-red .6s ease-in-out infinite',
        'flow': 'flow 1s linear infinite',
        'scanline': 'scanline 8s linear infinite',
        'blink': 'blink 1s steps(2, start) infinite',
      },
      keyframes: {
        'pulse-green': { '0%,100%': { opacity: 1 }, '50%': { opacity: .4 } },
        'pulse-red': { '0%,100%': { opacity: 1 }, '50%': { opacity: .2 } },
        'flow': { to: { strokeDashoffset: -20 } },
        'scanline': { '0%': { transform: 'translateY(-100%)' }, '100%': { transform: 'translateY(100%)' } },
        'blink': { to: { visibility: 'hidden' } },
      },
    },
  },
  plugins: [],
}

-------------------------------------------------
2. BASE LAYER (global.css)
-------------------------------------------------
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  body {
    @apply bg-void text-matrix-green font-mono text-xs leading-none antialiased;
    text-rendering: optimizeSpeed;
  }
  /* CRT mask */
  body::after {
    content: '';
    position: fixed;
    inset: 0;
    pointer-events: none;
    background: linear-gradient(rgba(18,16,16,0) 50%, rgba(0,0,0,0.25) 50%);
    background-size: 100% 2px;
    animation: scanline 8s linear infinite;
    z-index: 100;
  }
}

-------------------------------------------------
3. REACT COMPONENTS
-------------------------------------------------
// Wire.tsx – the actual “nervous-system” line
import { motion } from 'framer-motion'

type WireState = 'idle' | 'success' | 'failure' | 'processing'

const Wire: React.FC<{ state: WireState }> = ({ state }) => {
  const pathVariants = {
    idle: { pathLength: 1, opacity: .3, stroke: '#00FF41' },
    success: { pathLength: 1, opacity: 1, stroke: '#00FF41' },
    failure: { pathLength: 1, opacity: [1, 0, 1], stroke: '#FF3B30' },
    processing: { pathLength: 1, strokeDasharray: '4 4', stroke: '#FFCC00' },
  }

  return (
    <svg className="absolute inset-0 w-full h-full pointer-events-none">
      <motion.path
        d="M0 50 L100 50"
        variants={pathVariants}
        animate={state}
        transition={{
          duration: .6,
          repeat: state === 'processing' ? Infinity : 0,
          repeatType: 'loop',
        }}
        className="drop-shadow-glow"
        strokeWidth="2"
        strokeLinecap="round"
      />
    </svg>
  )
}

-------------------------------------------------
4. TEXT MODULE (with blinking cursor)
-------------------------------------------------
const ConsoleLine: React.FC<{ children: string }> = ({ children }) => (
  <div className="flex items-baseline">
    <span className="text-matrix-green">{children}</span>
    <span className="inline-block w-2 h-3 ml-1 bg-matrix-green animate-blink" />
  </div>
)

-------------------------------------------------
5. USAGE EXAMPLE
-------------------------------------------------
export default function LiveCircuit() {
  const [wire, setWire] = useState<'idle' | 'success' | 'failure' | 'processing'>('idle')

  return (
    <div className="relative w-full h-screen bg-void overflow-hidden">
      {/* HUD frame */}
      <div className="absolute inset-4 border border-matrix-green/30 rounded-none p-4">
        <ConsoleLine>scanning subsystem…</ConsoleLine>
      </div>

      {/* Nervous system */}
      <Wire state={wire} />

      {/* Demo buttons */}
      <div className="absolute bottom-8 left-8 space-x-2">
        <button onClick={() => setWire('success')} className="px-3 py-1 bg-matrix-green/20 border border-matrix-green text-matrix-green">Success</button>
        <button onClick={() => setWire('failure')} className="px-3 py-1 bg-alert-red/20 border border-alert-red text-alert-red">Failure</button>
        <button onClick={() => setWire('processing')} className="px-3 py-1 bg-processing-yellow/20 border border-processing-yellow text-processing-yellow">Process</button>
      </div>
    </div>
  )
}

-------------------------------------------------
6. MOTION STATES CHEAT-SHEET
-------------------------------------------------
idle        → opacity .3, static green
success     → opacity 1, drop-shadow-glow-green
failure     → flashing opacity 1↔0, drop-shadow-glow-red
processing  → strokeDasharray 4 4, drop-shadow-glow-yellow, infinite loop

-------------------------------------------------
7. PERFORMANCE NOTES
-------------------------------------------------
- SVG paths are GPU-accelerated; keep pathLength 1 to avoid layout thrash.
- Use `will-change: opacity` only on failure flash to prevent jank.
- CRT scanline is a single pseudo-element, 2 px high, GPU-friendly.

