```scss
/* ==========  TOKENS  ========== */
:root{
  --void:#050505;
  --matrix:#00FF41;
  --alert:#FF2E00;
  --pulse:#FFD700;
  --mono:"JetBrains Mono",monospace;
  --scan:url("#scanlines");
}

.line{                 /* physical SVG <path> */
  stroke-width:3;
  fill:none;
  stroke-linecap:round;
  stroke-linejoin:round;
}
.line--idle{ stroke:#222; }
.line--ok{ stroke:var(--matrix); filter:url(#glow); }
.line--fail{ stroke:var(--alert); filter:url(#glow); }
.line--proc{ stroke:var(--pulse); }

@keyframes flow{
  to{ stroke-dashoffset:-100; }
}
.line--proc{ stroke-dasharray:10 15; animation:flow 1s linear infinite; }

@keyframes blink{
  50%{ opacity:0; }
}
.cursor{ animation:blink 1s steps(2) infinite; }

/* CRT shader */
.crt::after{
  content:"";
  position:absolute;
  inset:0;
  background:var(--scan);
  mix-blend-mode:multiply;
  pointer-events:none;
}
```

```tsx
/* ==========  REACT COMPONENT  ========== */
import {motion,AnimatePresence} from "framer-motion";

export const LiveCircuit = ({state}) => {
  const d = "M10,90 C30,90 70,10 90,10"; // sample SVG path

  const variants = {
    idle:{pathLength:0,stroke:"#222"},
    ok:{pathLength:1,stroke:"var(--matrix)",transition:{duration:.4}},
    fail:{pathLength:1,stroke:"var(--alert)",transition:{duration:.2}},
    proc:{pathLength:1,stroke:"var(--pulse)",transition:{duration:.2}}
  };

  return (
    <svg className="w-full h-full" viewBox="0 0 100 100">
      <defs>
        <filter id="glow" x="-50%" y="-50%" width="200%" height="200%">
          <feGaussianBlur stdDeviation="3" result="blur"/>
          <feMerge>
            <feMergeNode in="blur"/>
            <feMergeNode in="SourceGraphic"/>
          </feMerge>
        </filter>
        <pattern id="scanlines" patternUnits="userSpaceOnUse" width="4" height="4">
          <rect width="4" height="2" fill="#000" opacity=".4"/>
        </pattern>
      </defs>

      <motion.path
        className="line"
        d={d}
        variants={variants}
        initial="idle"
        animate={state}
      />
    </svg>
  );
};
```

